			spDefRemain = spDefRemain % ITEM_SP_PURE_STR_BY_10_OFFSET;

			// 精錬値以上条件を満たさない場合は、次へ
			spDefRemain = CheckSpDefRefineOver(spDefRemain, eqpRefined);
			if (spDefRemain < 0) {
				continue;
			}

			// 精錬値が上がる度に条件を取得
			spDefRefineBy = Math.floor(spDefRemain / ITEM_SP_REFINE_BY_1_OFFSET);
